import React,{useState,useEffect} from 'react';
import axios from 'axios';
import logo from './logo.svg';
import FileSaver, {saveAs} from 'file-saver';
import './App.css';
import jsPDF from 'jspdf'



function App() {
  const token="Xn1XLxeLEUSFWXVDhTVHgXIn3SePtdiFRKbf7BKChrhKQ11lrzpxESqL1wGFSV4i"
  const [embalmers,setEmbalmers]=useState(null)
  const [isLoading,setIsLoading]=useState(true)
  const [month,setMonth]=useState(null)
  const [conservations,setConservations]=useState(null);
  const getData=async()=>{
    const data=await axios.get('http://localhost:3003/embalmer/list',{headers:{authorization:'Bearer '+token}});
    setEmbalmers(data.data);
    setIsLoading(false);
  }

  useEffect(()=>{
    getData();


  },[])

  const getPdf=(data)=>{
    var doc = new jsPDF()

    for(let i=0;i<data.length;i++){
      var str=["nom du thanatopracteur" +data[i].embalmer_firstname,"date de soin"+ data[i].date,"date de deces" +data[i].deceased_date,"nom du defunt"+data[i].deceased_firstname];
      doc.text(str,10,10);
    }


    doc.save('a4.pdf')
  }


  console.log(embalmers);


  if(isLoading===false){
  return (

    <div className="App">
       <h1>Hello World</h1> 




       {embalmers.map(function(element){

         return (<form method="post" onSubmit={async(event)=>{
           event.preventDefault();
           //console.log('form submitted');
           //console.log(token)
           //console.log(month);
           //console.log(typeof month);

           const conservationsByMonth=await axios.post('http://localhost:3003/summary/create/'+element.id,{month:month},{headers:{authorization:'Bearer '+token}}) 
           if(conservationsByMonth){
            setConservations(conservationsByMonth.data);
           }

           console.log(conservations);
           if(conservations!=null){
             getPdf(conservations);
           }

           /*

           const findData=await axios.get('http://localhost:3003/summary/get/'+element.id+'/'+month,{headers:{authorization:'Bearer '+token,contentType:'application/pdf'}},{responseType:'blob'});
           const pdfBlob=new Blob([findData.data],{type:'application/pdf'});
           saveAs(pdfBlob,'test.pdf');
           */


         }}> 
         <label> Choisir Le Mois Pour Le Thanato Practeur</label> 
       <label name="thanato"> {element.firstname+' '+element.lastname}</label>
          <select id={element.id} onChange={(event)=>{
            setMonth(event.target.value)
          }}>
            <option value="01"> 01</option>
            <option value="02"> 02</option>
            <option value="03"> 03</option>
            <option value="04"> 04</option>
            <option value="05"> 05</option>
            <option value="06"> 06</option>
            <option value="07"> 07</option>
            <option value="08"> 08</option>
            <option value="09"> 09</option>
            <option value="10"> 10</option>
            <option value="11"> 11</option>
            <option value="12"> 12</option>

          </select>
          <button type="submit"> Generer et Consulter le PDF </button>

         </form>)
       })}


    </div>

  );
  }

  else{
    return <div> Data Loading</div>
  }
}

export default App;
